      <Chart
        data={data}
        coord={{
          transposed: true,
        }}
      >
        <Axis field="月份" />
        <Axis field="月均降雨量" />
        <Interval x="月份" y="月均降雨量" color="name" adjust="stack" />
      </Chart>
      <Chart
        data={data}
        coord={{
          transposed: true,
        }}
