/** @jsx jsx */
import { jsx, Canvas, Chart, Area, Line, Axis, Legend } from '@antv/f2';

const context = document.getElementById('container').getContext('2d');

function formatterPercent(value) {
  value = value || 0;
  value = value * 100;
  return parseInt(value) + '%';
}

const data = [
  {
    country: 'Asia',
    year: '1750',
    value: 502,
    percent: 0.6511024643320363,
