  },
  {
    country: 'Africa',
    year: '1800',
    value: 107,
    percent: 0.11322751322751323,
  },
  {
    country: 'Europe',
    year: '1800',
    value: 203,
    percent: 0.21481481481481482,
  },
  {
    country: 'Asia',
    year: '1850',
    value: 809,
    percent: 0.6764214046822743,
  },
  {
    country: 'Africa',
    year: '1850',
